pragma solidity ^0.8.0;

import '@openzeppelin/contracts/utils/math/Math.sol';
import '@openzeppelin/contracts/utils/math/SafeMath.sol';
import '@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol';
import './interfaces/IRewardTreasury.sol';
import './interfaces/IPortfolioRegistry.sol';
import './interfaces/ILoyaltyProgram.sol';
import 'hardhat/console.sol';


contract LoyaltyProgram is ILoyaltyProgram {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;


    /* ========== STATE VARIABLES ========== */

    IERC20 private _baseToken;
    IRewardTreasury private _rewardTreasury;
    IPortfolioRegistry private _portfolioRegistry;


    /* ========== CONSTRUCTOR ========== */

    constructor(address baseToken, address rewardTreasury, address portfolioRegistry) {
        _baseToken = IERC20(baseToken);
        _rewardTreasury = IRewardTreasury(rewardTreasury);
        _portfolioRegistry = IPortfolioRegistry(portfolioRegistry);
    }


    /* ========== VIEWS ========== */

    /**
     * currently there is no token price. 
     * for the beginning all accounts are in manager tier.
     */
    function loyaltyTierOf(address account) external override view returns (LoyaltyTier) {
        return LoyaltyTier.PARTNER;
    }

    function rewardRateOf(address account) external override view returns (uint256) {
        LoyaltyTier tier = LoyaltyTier.PARTNER;

        if (tier == LoyaltyTier.PARTNER) {
            return 1500;
        } else if (tier == LoyaltyTier.MANAGER) {
            return 1250;
        } else if (tier == LoyaltyTier.SENIOR) {
            return 1000;
        } else if (tier == LoyaltyTier.ASSOCIATE) {
            return 750;
        }

        return 0;
    }

    function interestRateOf(address account) external override view returns (uint256) {
        LoyaltyTier tier = LoyaltyTier.PARTNER;

        if (tier == LoyaltyTier.PARTNER) {
            return 450;
        } else if (tier == LoyaltyTier.MANAGER) {
            return 350;
        } else if (tier == LoyaltyTier.SENIOR) {
            return 250;
        } else if (tier == LoyaltyTier.ASSOCIATE) {
            return 150;
        }

        return 0;
    }

    function discountRateOf(address account) external override view returns (uint256) {
        LoyaltyTier tier = LoyaltyTier.PARTNER;

        if (tier == LoyaltyTier.PARTNER) {
            return 2000;
        } else if (tier == LoyaltyTier.MANAGER) {
            return 1500;
        } else if (tier == LoyaltyTier.SENIOR) {
            return 1250;
        } else if (tier == LoyaltyTier.ASSOCIATE) {
            return 750;
        }

        return 0;
    }
